import { gql } from 'apollo-server-express';

import bookModel from './_model';

const Mutation = gql`
  extend type Mutation {
    addBook(title: String!, author: String!): Book
    deleteBook(id: String!): Book
  }
`;

export const mutationTypes = () => [Mutation];

