  Mutation: {
    addBook: async (_, { title, author }) => {
      const book = await bookModel.create({ title, author });
      return book.toGraph();
    },
    deleteBook: async (_, { id }) => {
      const book = await bookModel.findByIdAndRemove(id);
      return book ? book.toGraph() : null;
    }
  }
};

